package com.miramicodigo.textinputlayout;import android.support.design.widget.TextInputEditText;import android.support.design.widget.TextInputLayout;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.text.Editable;import android.text.TextWatcher;public class MainActivity extends AppCompatActivity {    private TextInputLayout errorInputLayout, customErrorInputLayout;    private TextInputEditText errorEditText, customErrorEditText;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        errorEditText = (TextInputEditText) findViewById(R.id.errorEditText);        errorInputLayout = (TextInputLayout) findViewById(R.id.errorInputLayout);        customErrorEditText = (TextInputEditText) findViewById(R.id.customErrorEditText);        customErrorInputLayout = (TextInputLayout) findViewById(R.id.customErrorInputLayout);        errorEditText.addTextChangedListener(new TextWatcher() {            @Override            public void beforeTextChanged(CharSequence s, int start, int count, int after) {            }            @Override            public void onTextChanged(CharSequence s, int start, int before, int count) {            }            @Override            public void afterTextChanged(Editable s) {                if (s.length() > errorInputLayout.getCounterMaxLength())                    errorInputLayout.setError("Caracteres maximo es " + errorInputLayout.getCounterMaxLength());                else                    errorInputLayout.setError(null);            }        });        customErrorEditText.addTextChangedListener(new TextWatcher() {            @Override            public void beforeTextChanged(CharSequence s, int start, int count, int after) {            }            @Override            public void onTextChanged(CharSequence s, int start, int before, int count) {            }            @Override            public void afterTextChanged(Editable s) {                if (s.length() > customErrorInputLayout.getCounterMaxLength())                    customErrorInputLayout.setError("Caracteres maximo es " + customErrorInputLayout.getCounterMaxLength());                else                    customErrorInputLayout.setError(null);            }        });    }}